mapper.update:
UPDATE announcementTag
announcementTagId=#{announcementTag.announcementTagId}, announcementTagName=#{announcementTag.announcementTagName}, announcementTagColor=#{announcementTag.announcementTagColor}, createUser=#{announcementTag.createUser}, createTime=#{announcementTag.createTime}, updateUser=#{announcementTag.updateUser}, updateTime=#{announcementTag.updateTime}, isEffective=#{announcementTag.isEffective},
WHERE announcementTagId = #{announcementTag.announcementTagId};

mapper.select:
SELECT count(*) FROM announcementTag a
AND a.announcementTagName like CONCAT ('%', #{map.announcementTagName}, '%')
AND a.isEffective=#{map.isEffective}

SELECT count(*) FROM announcementTag a
AND a.announcementTagName like CONCAT ('%', #{map.announcementTagName}, '%')
AND a.isEffective=#{map.isEffective}

SELECT
    a.*
FROM
    announcementTag a


        AND a.announcementTagName like CONCAT ('%', #{map.announcementTagName}, '%')


        AND a.isEffective=#{map.isEffective}



    ORDER BY
    a.${map.order} ${map.aOrS}


    limit #{map.start},#{map.limit}
