mapper.update:
UPDATE onDuty
onDutyId=#{onDuty.onDutyId}, onDutyUserName=#{onDuty.onDutyUserName}, onDutyTelephone=#{onDuty.onDutyTelephone}, onDutyStartTime=#{onDuty.onDutyStartTime}, onDutyEndTime=#{onDuty.onDutyEndTime}, createUser=#{onDuty.createUser}, createTime=#{onDuty.createTime}, updateUser=#{onDuty.updateUser}, updateTime=#{onDuty.updateTime}, isEffective=#{onDuty.isEffective},
WHERE onDutyId = #{onDuty.onDutyId};

mapper.select:
SELECT

FROM
onDuty a


        AND a.onDutyUserName like CONCAT ('%', #{map.onDutyUserName}, '%')


        AND a.isEffective=#{map.isEffective}


        AND ( a.onDutyStartTime between #{map.onDutyStartTime} and #{map.onDutyEndTime}
        OR a.onDutyEndTime between #{map.onDutyStartTime} and #{map.onDutyEndTime} )



    ORDER BY
    a.${map.order} ${map.aOrS}


    limit #{map.start},#{map.limit}
