mapper.update:
UPDATE user
auth=#{user.auth}, classNum=#{user.classNum}, createDay=#{user.createDay}, grade=#{user.grade}, image=#{user.image}, isEffective=#{user.isEffective}, mobile=#{user.mobile}, openId=#{user.openId}, password=#{user.password}, realname=#{user.realname}, studentId=#{user.studentId}, userId=#{user.userId}, username=#{user.username}, unionid=#{user.unionid},
WHERE userId = #{userId};

mapper.select:
SELECT

FROM
user tt
WHERE
userId = #{userId}
LIMIT 1;

SELECT

FROM
user a


		AND a.userId=#{map.userId}


		AND a.userName like CONCAT ('%', #{map.userName}, '%')


		AND a.studentId=#{map.studentId}


		AND a.grade=#{map.grade}


		AND a.telephone=#{map.telephone}


		AND a.realName=#{map.realName}


		AND a.openId=#{map.openId}


		AND a.isEffective=#{map.isEffective}



	ORDER BY
	a.${map.order} ${map.aOrS}


	limit #{map.start},#{map.limit}

SELECT
a.*, b.createDate, b.competitionId, c.competitionTitle
FROM
(user a inner join applycompetition b on a.userId=b.joinUser)
INNER JOIN competition c on c.competitionId=b.competitionId


		AND c.competitionId=#{map.competitionId}


		AND a.isEffective=#{map.isEffective}


		AND b.isEffective=#{map.isEffective}


		AND c.isEffective=#{map.isEffective}



	ORDER BY
	b.${map.order} ${map.aOrS}


	limit #{map.start},#{map.limit}

SELECT
count(*)
FROM
(user a inner join applycompetition b on a.userId=b.joinUser)
INNER JOIN competition c on c.competitionId=b.competitionId


		AND c.competitionId=#{map.competitionId}


		AND a.isEffective=#{map.isEffective}


		AND b.isEffective=#{map.isEffective}


		AND c.isEffective=#{map.isEffective}

SELECT
a.*, b.createDate, b.LectureId, c.lectureTitle
FROM
(user a inner join applylecture b on a.userId=b.joinUser)
INNER JOIN lecture c on c.lectureId=b.lectureId


		AND c.lectureId=#{map.lectureId}


		AND a.isEffective=#{map.isEffective}


		AND b.isEffective=#{map.isEffective}


		AND c.isEffective=#{map.isEffective}



	ORDER BY
	b.${map.order} ${map.aOrS}


	limit #{map.start},#{map.limit}

SELECT
count(*)
FROM
(user a inner join applylecture b on a.userId=b.joinUser)
INNER JOIN lecture c on c.lectureId=b.LectureId


		AND c.lectureId=#{map.lectureId}


		AND a.isEffective=#{map.isEffective}


		AND b.isEffective=#{map.isEffective}


		AND c.isEffective=#{map.isEffective}

mapper.update:
UPDATE user
userId=#{user.userId}, userName=#{user.userName}, password=#{user.password}, auth=#{user.auth}, avatar=#{user.avatar}, studentId=#{user.studentId}, grade=#{user.grade}, createTime=#{user.createTime}, telephone=#{user.telephone}, realName=#{user.realName}, openId=#{user.openId}, isEffective=#{user.isEffective},
WHERE userId = #{user.userId};

mapper.select:
SELECT
	a.*
FROM
	user a


		AND a.userId=#{map.userId}


		AND a.userName like CONCAT ('%', #{map.userName}, '%')


		AND a.studentId like CONCAT ('%', #{map.studentId}, '%')


		AND a.grade=#{map.grade}


		AND a.telephone=#{map.telephone}


		AND a.auth & #{map.auth} != 0


		AND a.realName like CONCAT ('%', #{map.userName}, '%')


		AND a.openId=#{map.openId}


		AND a.isEffective=#{map.isEffective}



	ORDER BY
	a.${map.order} ${map.aOrS}


	limit #{map.start},#{map.limit}

SELECT a.* FROM user a
AND a.userName like CONCAT ('%', #{map.userName}, '%')
AND a.studentId like CONCAT ('%', #{map.studentId}, '%')
AND a.realName like CONCAT ('%', #{map.realName}, '%')
AND a.auth & #{map.auth} != 0
AND a.isEffective=#{map.isEffective}
ORDER BY a.${map.order} ${map.aOrS}
limit #{map.start}, #{map.limit}
