mapper.update:
UPDATE user
auth=#{user.auth}, classNum=#{user.classNum}, createDay=#{user.createDay}, grade=#{user.grade}, image=#{user.image}, isEffective=#{user.isEffective}, mobile=#{user.mobile}, openId=#{user.openId}, password=#{user.password}, realname=#{user.realname}, studentId=#{user.studentId}, userId=#{user.userId}, username=#{user.username}, unionid=#{user.unionid},
WHERE userId = #{userId};

mapper.select:
SELECT 

FROM 
	user tt
WHERE 
	userId = #{userId}
LIMIT 1;

SELECT

FROM 
user tt


		AND tt.userId=#{map.userId}


		AND tt.userName like CONCAT ('%', #{map.userName}, '%')


		AND tt.studentId=#{map.studentId}


		AND tt.grade=#{map.grade}


		AND tt.telephone=#{map.telephone}


		AND tt.realName=#{map.realName}


		AND tt.openId=#{map.openId}


		AND tt.isEffective=#{map.isEffective}



ORDER BY 
	tt.${map.order} ${map.aOrS}


	limit #{map.start},#{map.limit}

SELECT

FROM
	user a


		AND tt.userId=#{map.userId}


		AND tt.userName like CONCAT ('%', #{map.userName}, '%')


		AND tt.studentId=#{map.studentId}


		AND tt.grade=#{map.grade}


		AND tt.telephone=#{map.telephone}


		AND tt.realName=#{map.realName}


		AND tt.openId=#{map.openId}


		AND tt.isEffective=#{map.isEffective}



ORDER BY
	a.${map.order} ${map.aOrS}


limit #{map.start},#{map.limit}

SELECT count(*) FROM user a
AND tt.userName like CONCAT ('%', #{map.userName}, '%')
AND tt.studentId=#{map.studentId}
AND tt.isEffective=#{map.isEffective}

SELECT

FROM 
	user tt


		AND tt.userName like CONCAT ('%', #{map.userName}, '%')


		AND tt.studentId=#{map.studentId}


		AND tt.isEffective=#{map.isEffective}



	ORDER BY
		tt.${map.order} ${map.aOrS}


	limit #{map.start}, #{map.limit}

		SELECT
		a.*, b.createDate, b.competitionId, c.competitionTitle
		FROM
			(user a inner join applycompetition b on a.userId=b.joinUser)
            INNER JOIN competition c on c.competitionId=b.competitionId


                             AND c.competitionId=#{map.competitionId}


            				AND a.isEffective=#{map.isEffective}


                            AND b.isEffective=#{map.isEffective}


                            AND c.isEffective=#{map.isEffective}



            		ORDER BY
            			b.${map.order} ${map.aOrS}


            		limit #{map.start},#{map.limit}

		SELECT
		count(*)
		FROM
			(user a inner join applycompetition b on a.userId=b.joinUser)
            INNER JOIN competition c on c.competitionId=b.competitionId


                             AND c.competitionId=#{map.competitionId}


            				AND a.isEffective=#{map.isEffective}


                            AND b.isEffective=#{map.isEffective}


                            AND c.isEffective=#{map.isEffective}

		SELECT
		a.*, b.createDate, b.LectureId, c.lectureTitle
		FROM
			(user a inner join applylecture b on a.userId=b.joinUser)
            INNER JOIN lecture c on c.lectureId=b.lectureId


                             AND c.lectureId=#{map.lectureId}


            				AND a.isEffective=#{map.isEffective}


                            AND b.isEffective=#{map.isEffective}


                            AND c.isEffective=#{map.isEffective}



            		ORDER BY
            			b.${map.order} ${map.aOrS}


            		limit #{map.start},#{map.limit}

		SELECT
		count(*)
		FROM
			(user a inner join applylecture b on a.userId=b.joinUser)
                                INNER JOIN lecture c on c.lectureId=b.LectureId


                             AND c.lectureId=#{map.lectureId}


            				AND a.isEffective=#{map.isEffective}


                            AND b.isEffective=#{map.isEffective}


                            AND c.isEffective=#{map.isEffective}

		select aa.* from user aa LEFT JOIN impression bb on aa.userId=bb.imUser


                                                   AND aa.isEffective=#{map.isEffective}


                                  				AND aa.username like CONCAT ('%', #{map.username}, '%')



     group by aa.userId order by (COALESCE(sum(bb.agreeNum),0)+count(bb.agreeNum)*0.1) DESC

                          		limit #{map.start},#{map.limit}
